import React from 'react'; //import 'React' default export, and { Component } non-default export from react
// App.css was a hangover from the create-react-app, it's not really needed for this basic example
import axios from "axios";
import Button from "@material-ui/core/Button";
import Distance from './Distance'
import {connect} from 'react-redux';

function App(appState) {
  const [location, setLocation] = React.useState(null);
  const [rest, setRest] = React.useState(null);
  const [page, setPage] = React.useState(`start`);

const mapStateToProps = (state) => ({
  appState: state,
});


  React.useEffect(() => {
    var location;
    /*{
      questionText: "Question",
      answer: false,
      answerKey: "",
      questionType: "radio",
      options: [{ optionText: "Option 1" }],
      open: true,
      required: false,
    };
    */
  }
  )

  function handlePage(newPage) {
    setPage(newPage);
  }

  function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }

  function makeCall(quer){
    //console.log(quer);
  axios.get(`http://localhost:8000/results`, { params: quer})
    .then(res => {
      var businesses = [];
      //console.log(res.data);
      for (const business of res.data) { 
        //console.log(business.name); 
        businesses.push(business);
      }
      const result = businesses[getRandomInt(businesses.length)].name;
      console.log(result);
      setRest(result);
      })
      .catch(function(error) {
        console.log(error);
    });

    return () => {
      console.log('Will unmount');
    };
  } // Empty array means to only run once on mount.

  function handleClick(){
    makeCall({location: location});
    setPage('distance');
  }

  if(page === 'start'){
  return (
    <div>
      <div>
        <h1>FEAT</h1>
        <input 
          type="text"
          placeholder={"eg. Baltimore, 93117"}
          onChange={(e) => {
            setLocation(e.target.value);
          }}>
          </input>
      </div>
      <div>
        <Button onClick={handleClick}>
          Start
        </Button>
      </div>
    </div>
  );
}
else if (page === 'distance'){
  return(<Distance pageProp={page} locationProp={location} changePage={handlePage}/ >);
}
}

export default connect(mapStateToProps)(App);
