const initialState = {
    location: "",
    page: 'start'
}

const appReducer = (state = initialState, action) => {
    switch(action.type){
    case 'LOC_CHANGED': 
    // do something
    return {
        ...state, 
        location: action.payload
    };

    case 'PAGE_CHANGED': 
    // do something
    return {
        ...state, 
        location: action.payload
    };


    default:
    return state;
    }
}

export default appReducer;
